let footerLinks=document.getElementsByClassName("footer-links")[0],display=window.getComputedStyle(footerLinks,null).display,shareIcon=document.getElementById("share-icon-img"),btn=document.getElementById("share-button");const darkenShareBtn=()=>{btn.style.backgroundColor="hsl(214, 17%, 51%)",shareIcon.src="./images/icon-share-white.svg"},lightenShareBtn=()=>{btn.style.backgroundColor="hsl(210, 46%, 95%)",shareIcon.src="./images/icon-share-gray.svg"};function onClick(){"none"==display?(display=footerLinks.style.display="flex",darkenShareBtn()):(display=footerLinks.style.display="none",lightenShareBtn())}