const signupForm=document.forms["signup-form"],inputs=["first-name","last-name","email","password"],placeholders={"first-name":"First Name","last-name":"Last Name",email:"Email Address",password:"Password"};function defaultInput(e){let n=signupForm[e];n.style.border="1px solid hsl(246, 25%, 77%)",n.placeholder=placeholders[n.name],n.previousElementSibling.style.display="none",n.nextElementSibling.style.display="none",n.classList.contains("red-placeholder")&&(n.classList.remove("red-placeholder"),n.nextElementSibling.innerHTML="<em>Email cannot be empty</em>")}function errorInput(e){let n=signupForm[e];n.style.border="1.5px solid hsl(0, 100%, 74%)",n.placeholder="",n.previousElementSibling.style.display="inline",n.nextElementSibling.style.display="block","email"==n.name&&(n.nextElementSibling.innerHTML="<em>Email cannot be empty</em>"),""!=n.value&&(n.placeholder="email@example.com",n.value="",n.nextElementSibling.innerHTML="<em>Looks like this is not an email</em>",n.classList.add("red-placeholder"))}function onFocus(e){defaultInput(e.name)}function validateForm(){for(let e=0;e<4;e++)(""==signupForm[inputs[e]].value||2==e&&!/\w+@\w+\.\w+/.test(signupForm[inputs[e]].value))&&errorInput(inputs[e])}signupForm.addEventListener("submit",e=>{e.preventDefault()});let formContainer=document.getElementsByClassName("form-container")[0];document.addEventListener("click",e=>{if(!formContainer.contains(e.target))for(let e=0;e<4;e++)defaultInput(inputs[e])});